package com.mycompany.tpARG;import java.io.IOException;import java.io.InputStream;import java.util.Properties;public class Configuracion {    private static Properties propiedades;    static {        try {            propiedades = new Properties();            InputStream inputStream = Configuracion.class.getClassLoader().getResourceAsStream("config.properties");            propiedades.load(inputStream);        } catch (IOException e) {           System.out.println( e.getMessage());        }    }    public static int getPuntosPorAcierto() {        return Integer.parseInt(propiedades.getProperty("puntosPorAcierto"));    }    public static int getPuntosPorAciertoRonda() {        return Integer.parseInt(propiedades.getProperty("puntosPorAciertoRonda"));    }    public static int getPuntosPorAciertoFases() {        return Integer.parseInt(propiedades.getProperty("puntosPorAciertoFases"));    }}